mapper.select:
SELECT t.id
FROM tasks t
WHERE t.user_id = #{userId}

mapper.update:
UPDATE tasks
SET title = #{title},
description = #{description},
expiration_date = #{expirationDate},
status = #{status}
WHERE id = #{id}

mapper.select:
SELECT exists(
SELECT 1
FROM tasks
WHERE user_id = #{user_id}
AND id = #{id}
)
